function TaskForm({ tasks, addTask, deleteTask, editTask }) {
    const [task, setTask] = useState('');
    const [editing, setEditing] = useState(null);
  
    // Handle task input change
    const handleInputChange = (e) => {
      setTask(e.target.value);
    };
  
    // Handle task completion
    const handleCompletion = (taskToComplete) => {
      const updatedTasks = tasks.map((task) =>
        task === taskToComplete ? { ...task, completed: !task.completed } : task
      );
      setTasks(updatedTasks);
    };
  
    // Handle form submission
    const handleSubmit = (e) => {
      e.preventDefault();
      if (task.trim()) {
        if (editing) {
          editTask(editing, task);
          setEditing(null);
        } else {
          addTask({ text: task, completed: false });
        }
        setTask('');
      }
    };
  
    return (
      <div>
        <h2>Task Form</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={task}
            onChange={handleInputChange}
            placeholder="Enter task"
          />
          <button type="submit">{editing ? 'Update Task' : 'Add Task'}</button>
        </form>
  
        <h3>Task List</h3>
        <ul>
          {tasks.map((task, index) => (
            <li key={index} style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleCompletion(task)}
              />
              {task.text}
              <button onClick={() => handleEdit(task.text)}>Edit</button>
              <button onClick={() => handleDelete(task.text)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    );
  }
