import React, { useState, useEffect } from 'react';

function TaskForm({ tasks, addTask, deleteTask, editTask }) {
  const [task, setTask] = useState('');
  const [editing, setEditing] = useState(null); // To track which task is being edited

  // Handle task input change
  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  // Handle form submission (adding or updating a task)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (task.trim()) {
      if (editing) {
        editTask(editing, task); // Update the task
        setEditing(null); // Reset editing state
      } else {
        addTask(task); // Add new task
      }
      setTask(''); // Clear input field after submitting
    }
  };

  // Handle task edit (sets up the task for editing)
  const handleEdit = (taskToEdit) => {
    setTask(taskToEdit);
    setEditing(taskToEdit); // Set the task being edited
  };

  // Handle task deletion
  const handleDelete = (taskToDelete) => {
    deleteTask(taskToDelete); // Delete the task
  };

  return (
    <div>
      <h2>Task Form</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={task}
          onChange={handleInputChange}
          placeholder="Enter task"
        />
        <button type="submit">{editing ? 'Update Task' : 'Add Task'}</button>
      </form>

      <h3>Task List</h3>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {task}
            <button onClick={() => handleEdit(task)}>Edit</button>
            <button onClick={() => handleDelete(task)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskForm;
